import React, { useEffect, useState } from 'react'
import {Header} from '../components/Header'
import ContactsServices from '../Firebase/services'
import { collection, query, where } from 'firebase/firestore';  
import { db } from '../Firebase/firebase'
import { UserAuth } from '../context/AuthContext';
const contactsCollection = collection(db,'contacts')

export default function ContactView() {
  const [contactData, setContactData] = useState([])
  const url = window.location.href
  const contactId = url.split("?")[1] 
  const {user, logout} = UserAuth();
  const userID = user.uid

  useEffect(()=>{
    getContact();
  },[])
  
  const getContact = async ()=>{
    const q = query(contactsCollection, where("uid","==", userID), where("id","==",contactId))
            const data = await ContactsServices.getAllUserData(q);
            setContactData(data.docs.map((doc) => ({ ...doc.data(), id: doc.id })));
  }
  console.log(contactId)
  console.log(userID)
  return (
  <>
    <Header></Header>
    <div>ContactView</div>
  </>
  )
}
