import React from 'react'
import btlogo from '../images/btlogo.png'
import bttopper from '../images/bt_topper.png'
import { UserAuth } from '../context/AuthContext'
import { useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

export function Header() {
    const {user, logout} = UserAuth();
    const navigate = useNavigate();
    const [menu, setMenu] = useState('');

        
    const handleLogout = async () => {
      try {
        await logout();
        navigate('/');
        console.log('You are logged out')
      } catch (e) {
        console.log(e.message);
      }
    };
    
    const ShowMenu = () => {
      if(!menu){
        setMenu('true')
      }else{
        setMenu('')}
      console.log(menu)
    }

  return (
    <>
    <a onClick={ShowMenu} className='logo'><img id='menubtn' src={btlogo}/></a>
      <nav style={!menu ? {left:'0'} : {left:'-100%'}} >
          <div>
              <a onClick={ShowMenu} className='logo'><img id='logo' src={bttopper}/></a>
          </div>
          <div className=''>
              {!user ? <a
               href='/'>Main Screen</a> : ""}
              {!user ? <a onClick={ setMenu('')}>Sign In</a> : ""}
              {!user ? <a href='/signup'>Sign up</a> : ""}
              {!user ? "" : <a href='/account'>Dashboard</a>}
              {!user ? "" : <a onClick={handleLogout}>Logout</a>}
        </div>
      </nav>
    </>
  )
}
